<?class categoryM extends model{		static function getTypes(){		return array('0'=>'Новости','1'=>'Игры','2'=>'Персонажи');	}	static function add($data){				if(!$data['h1'] ){			self::setMessage('h1 должен быть заполнены');			return false;		}				$data=self::filt($data,array('h1','title','text'));		$data['url']=str::translit($data['h1']);		$tb= new table('category');		$id=$tb->insert($data);		return true;	}		static function change($data){			if(!$data['h1']){			self::setMessage('h1 должен быть заполнены');			return false;		}				$id=(int)$data['id'];		$data=self::filt($data,array('h1','title','text'));		$tb= new table('category');		$tb->update($data,$id);		return true;	}		static function delete($id){		$tb=new table('category');		$tb->delete($id);	}}?>