<?if($update){?><form method='post' >	<table  cellpadding='5' cellspacing='0' width='100%'>		<tr>			<td width='1'>Новость</td>			<td>				<?echo form::select($list,$id,'name="id"','id','h1')?><input type='submit' name='update' value='Изменить' /><input type='submit' name='delete' class='delbut' value='Удалить' />			</td>		</tr>	</table></form><?}?><?if(!$update || $id){$action='addnewsaction';if($update)$action='changenewsaction';?><form method='post' action='/action/admin/<?=$action?>/' enctype="multipart/form-data"><?if($id){?><input type='hidden' name='id' value='<?=$id?>' /><?}?><table  cellpadding='5' cellspacing='0' width='100%'>	<tr>		<td>Заголовок (h1):</td><td><textarea name='h1'><?=$item['h1']?></textarea></td>	</tr>	<tr>		<td>Title:</td><td><textarea name='title'><?=$item['title']?></textarea></td>	</tr>	<tr>		<td>Изображение</td>		<td>			<input type='file' name='img' />			<?				if($id && $img=image::get('/uploads/news/'.$id.".jpg")){					?>					<br /><img src='<?=$img->cropPath?>' />					<?				}							?>		</td>			</tr>		<tr>		<td>Категория:</td>		<td>		<?				if(!$update || !$item['category']){				echo form::select($catlist,0,"name='category[]' class='copyCont'",'id','h1');			}else{				foreach($item['category'] as $row){					echo form::select($catlist,$row['id'],"name='category[]' class='copyCont' data-family='cat'",'id','h1');				}			}					?>		</td>	</tr>		<tr>		<td>Игра</td><td><textarea name='game'></textarea><input type='button' value='Проверить' id='checkgame' style='cursor:pointer'/><div id='checkstate'></div></td>	</tr>	<tr>		<td>Ширина игры</td><td><input type='text' name='gamewidth' value='<?=$item['gamewidth']?>'/></td>	</tr>	<tr>		<td>Игра (Дополнение)</td><td><textarea name='gametext'><?=$item['gametext']?></textarea></td>	</tr>	<tr>		<td>Видео к игре (ссылка)</td><td><textarea name='gamevid'><?=$item['gamevid']?></textarea></td>	</tr>	<tr>		<td>Управление</td><td><textarea name='controls'><?=$item['controls']?></textarea></td>	</tr>	<tr>		<td colspan='2'>Текст <br /><textarea name='text'><?=$item['text']?></textarea></td>		<script>   CKEDITOR.replace( 'text' );</script>	</tr>	<tr>		<td>Сортировка:</td><td><input type='text' name='order' value='<?=$item['order']?>' /></td>	</tr>	<tr>		<td></td><td><input type='submit' value='<?if($update){?>Изменить<?}else{?>Добавить<?}?>'></td>	</tr>		<tr></table></form><?}?>